mapper.select:
SELECT * FROM USER_PRO_HISTORY
pro_num = #{pro_num} and user_id = #{user_id}
order by desc

SELECT pro_eval_content, pro_eval_score, pro_eval_date
FROM pro_eval
WHERE pro_num = #{pro_num}

SELECT pro_num
FROM pro_eval
WHERE user_id = #{user_id} and pro_num = #{pro_num}

mapper.update:
UPDATE pro_eval 
SET pro_eval_content = #{pro_eval_content}, pro_eval_score = #{pro_eval_score}, pro_eval_date = sysdate
WHERE pro_num = #{pro_num} and user_id = #{user_id}
